function C = cayley2rotbar(s)

C = ( (1-s'*s) * eye(3) + 2 * skewsymm(s) + 2 * (s * s'))';

end

function C = skewsymm(X1)
% generates skew symmetric matrix

C = [0      , -X1(3) ,  X1(2)
    X1(3) , 0      , -X1(1)
    -X1(2) , X1(1)  ,     0];
end

